using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace Student_Manager__ConsoleApp_
{
    /// <summary>
    /// Class decribe all attribute of a student
    /// </summary>
    internal class Student
    {
        /// <summary>
        /// unic ID of student's generated by GUID
        /// </summary>
        public string ID { get; set; }
        /// <summary>
        /// Studen's name
        /// </summary>
        public string StudentName { get; set; }
        /// <summary>
        /// Student's Age
        /// </summary>
        public int Age { get; set; }
        /// <summary>
        /// Student's Gender
        /// </summary>
        public string Gender { get; set;}

        /// <summary>
        /// Create new Student object with params (Name, Age, Gender)
        /// </summary>
        /// <param name="studentName"></param>
        /// <param name="age"></param>
        /// <param name="gender"></param>
        public Student(string studentName, int age, string gender) {
            ID = Guid.NewGuid().ToString();
            StudentName = studentName;
            Age = age;
            Gender = gender;           
        }
        /// <summary>
        /// Check equality of studen's name
        /// </summary>
        /// <param name="name"></param>
        /// <returns>'true' if sended Name is equal to Object's; otherwise, 'false'</returns>
        public bool EqualsName(string name)
        { 
            return StudentName == name;
        }
        /// <summary>
        /// Check equality of studen's ID
        /// </summary>
        /// <param name="name"></param>
        /// <returns>'true' if sended ID is equal to Object's; otherwise, 'false'</returns>
        public bool EqualsID(string id)
        {
            return ID == id;
        }
        /// <summary>
        /// Convert full info of student to string
        /// </summary>
        /// <returns>Info of student as 'string'</returns>
        public override string ToString()
        {
            return $"\nID: {ID}\nName: {StudentName}\nAge: {Age}\nGender: {Gender}";
        }
        /// <summary>
        /// Update info of a student to sended
        /// </summary>
        /// <param name="studentName"></param>
        /// <param name="age"></param>
        /// <param name="gender"></param>
        public void UpdateInfo(string studentName, int age, string gender)
        {
            if (studentName != "EmptyField")
            {
                StudentName = studentName;
            }
            if (age != 666)
            {
                Age = age;
            }
            if (gender != "EmptyField")
            {
                Gender = gender; ;
            }
        }
        /// <summary>
        /// Update ID of Student to sended
        /// </summary>
        /// <param name="id"></param>
        public void CopyStudentID(string id)
        {
            ID = id;
        }
        
    }
}
